import React, {Component} from 'react'
import {Col, Container, Row} from "reactstrap";
import fetch from 'isomorphic-unfetch';
import $ from 'jquery';

class Sosmed extends Component {
    constructor(props) {
        super(props);
        this.state = {
            image_url: '',
            login: '',
            name: '',
        }
    }

    componentDidMount() {
        fetch('https://api.github.com/users/adamarthurf')
            .then(response => {
                if (response.ok) {
                    return response.json()
                }
                throw new Error(response.statusText)
            })
            .then(response => {
                this.setState({
                    avatar_url: response.avatar_url,
                    login: response.login,
                    name: response.name,
                })
            })
    }

    render() {
        return (
            <section className="sosmed" id="sosmed">
                <Container className="themed-container">
                    <Row>
                        <Col md='12'>
                            <h2 className="text-center font-weight-bold text-light">Social Media</h2>
                        </Col>
                    </Row>
                    <Row className='justify-content-center'>
                        <Col md='6'>
                            <Row className="text-light">
                                <Col md="4">
                                    <img src={this.state.avatar_url} alt={this.state.login} className='rounded-circle img-thumbnail'/>
                                </Col>
                                <Col md="8">
                                    <h3>{this.state.name}</h3>
                                </Col>
                            </Row>
                        </Col>
                        <Col md='6'>

                        </Col>
                    </Row>
                </Container>
            </section>)
    }
}

export default Sosmed;
